import { useState } from 'react';
import '../style.less'
function Header() {
  const [current, setcurrent] = useState(1)
  const [navList] = useState<Array<any>>([
    { code: 1, name: '官网首页' },
    { code: 2, name: '案例展示' },
    { code: 3, name: '关于我们' },
    { code: 4, name: '联系我们' },
  ])
  return (
    <div id='header'>
      <div className='title'>
        <span>领业装饰</span>
        <span>LINGYE</span>
      </div>
      <div className='nav'>
        {
          navList && navList.map((item: any) => {
            return <a
              key={item.code}
              className={current === item.code ? 'active' : ''}
              onClick={() => setcurrent(item.code)}
            >{item.name}</a>
          })
        }
      </div>
    </div>
  )
}
export default Header;
